INSERT INTO `projects` (`id`, `name`, `builderName`, `description`, `location`, `city`, `latitude`, `longitude`, `status`, `priceRange`, `minPrice`, `maxPrice`, `configurations`, `reraNumber`, `possessionDate`, `totalUnits`, `towers`, `floors`, `coverImage`, `videoUrl`, `brochureUrl`, `featured`, `createdAt`, `updatedAt`, `masterPlanUrl`, `builderDescription`, `builderLogo`, `builderEstablished`, `builderProjects`, `slug`, `badge`, `customBadgeText`) VALUES (1, 'Pride Purple Park Eden', 'Pride Group', 'Pride Purple Park Eden offers premium 2 & 3 BHK apartments in the heart of Kharadi. With world-class amenities and excellent connectivity to IT hubs, this project is perfect for modern families.', 'Kharadi', 'Pune', '18.56790000', '73.91430000', 'Under Construction', '₹85L - ₹1.45Cr', '8500000.00', '14500000.00', '2, 3 BHK', 'P52100017620', '2028-12-01 05:00:00', 450, 3, 18, '/images/projects/project-1.jpg', 'https://www.youtube.com/watch?v=CUYFXeA-Rj4', 'https://www.pridepurpleproperties.com/brochure/park-eden-brochure.pdf', 1, '2026-01-25 07:12:09', '2026-01-25 11:57:45', NULL, 'Pride Purple Group is one of Pune\'s most trusted real estate developers, established in 1995. With over 25 years of excellence, they have delivered 50+ residential and commercial projects across Pune. Known for innovative designs, quality construction, and timely delivery, Pride Purple has earned the trust of over 15,000 happy homeowners. Their projects are characterized by premium amenities, strategic locations, and sustainable building practices.', '/images/builders/pride-group.jpg', 1995, 50, 'pride-purple-park-eden', 'Best Seller', 'Pre-Launch');
INSERT INTO `projects` (`id`, `name`, `builderName`, `description`, `location`, `city`, `latitude`, `longitude`, `status`, `priceRange`, `minPrice`, `maxPrice`, `configurations`, `reraNumber`, `possessionDate`, `totalUnits`, `towers`, `floors`, `coverImage`, `videoUrl`, `brochureUrl`, `featured`, `createdAt`, `updatedAt`, `masterPlanUrl`, `builderDescription`, `builderLogo`, `builderEstablished`, `builderProjects`, `slug`, `badge`, `customBadgeText`) VALUES (2, 'Kolte Patil 24K Glitterati', 'Kolte Patil Developers', '24K Glitterati is a premium residential project offering luxurious 3 & 4 BHK apartments with world-class amenities. Located in the heart of Kharadi, this project features Italian marble flooring, modular kitchens, and smart home automation. The project is surrounded by lush greenery and offers panoramic views of the city.', 'Kharadi', 'Pune', '18.59120000', '73.73890000', 'Ready to Move', '1.8 Cr - 3.2 Cr', '18000000.00', '32000000.00', '3, 4 BHK', 'P52100025678', '2024-06-01 04:00:00', 180, 2, 28, '/images/projects/project-2.jpg', 'https://www.youtube.com/watch?v=u1ljDLF_goA', 'https://www.koltepatil.com/brochure/24k-glitterati-brochure.pdf', 1, '2026-01-25 07:18:39', '2026-01-25 09:47:03', NULL, 'Kolte-Patil Developers Ltd. is a leading real estate company in Pune, established in 1991. Listed on BSE and NSE, they have delivered over 100 projects spanning 20+ million sq.ft. across residential, commercial, and IT parks. Known for their commitment to quality, transparency, and customer satisfaction, Kolte-Patil has won numerous awards including the \'Most Trusted Brand\' in real estate. Their 24K series represents ultra-luxury living with world-class amenities.', '/images/partners/kolte-patil.png', 1991, 100, 'godrej-infinity', NULL, NULL);
INSERT INTO `projects` (`id`, `name`, `builderName`, `description`, `location`, `city`, `latitude`, `longitude`, `status`, `priceRange`, `minPrice`, `maxPrice`, `configurations`, `reraNumber`, `possessionDate`, `totalUnits`, `towers`, `floors`, `coverImage`, `videoUrl`, `brochureUrl`, `featured`, `createdAt`, `updatedAt`, `masterPlanUrl`, `builderDescription`, `builderLogo`, `builderEstablished`, `builderProjects`, `slug`, `badge`, `customBadgeText`) VALUES (3, 'Kumar Privé', 'Kumar Properties', 'Kumar Privé offers thoughtfully designed 2 & 3 BHK apartments in the rapidly developing Kharadi area. With excellent connectivity to IT parks and the airport, this project is ideal for working professionals. Features include Vastu-compliant designs, ample natural light, and modern amenities.', 'Kharadi', 'Pune', '18.62980000', '73.79970000', 'Under Construction', '95 L - 1.65 Cr', '9500000.00', '16500000.00', '2, 3 BHK', 'P52100034567', '2026-03-01 05:00:00', 320, 4, 22, 'https://d2xsxph8kpxj0f.cloudfront.net/310419663026719415/aSHZEBwUmWTuA7HAwuTg3z/property-images/1769353551908-NCMJPJ8U0P.jpeg', 'https://www.youtube.com/watch?v=T3KGtgWEGW0', 'https://d2xsxph8kpxj0f.cloudfront.net/310419663026719415/aSHZEBwUmWTuA7HAwuTg3z/property-images/1769356013132-mJvu1y732I.pdf', 0, '2026-01-25 07:18:43', '2026-01-25 20:55:16', 'https://d2xsxph8kpxj0f.cloudfront.net/310419663026719415/aSHZEBwUmWTuA7HAwuTg3z/property-images/1769353562290-80tZlAMVCo.jpeg', 'Kumar Properties is one of Pune\'s most trusted and established real estate developers, founded in 1966. With over 55 years of excellence, they have delivered 140+ projects and created homes for 42,000+ happy families across 36 million sq.ft. Known for their commitment to quality, innovation, and customer-centric approach, Kumar Properties has won numerous awards including \'Developer of the Year\'. Their projects are synonymous with premium living and sustainable development.', '/images/partners/kumar-properties.png', 1966, 140, 'kolte-patil-life-republic', 'Special Offer', NULL);
INSERT INTO `projects` (`id`, `name`, `builderName`, `description`, `location`, `city`, `latitude`, `longitude`, `status`, `priceRange`, `minPrice`, `maxPrice`, `configurations`, `reraNumber`, `possessionDate`, `totalUnits`, `towers`, `floors`, `coverImage`, `videoUrl`, `brochureUrl`, `featured`, `createdAt`, `updatedAt`, `masterPlanUrl`, `builderDescription`, `builderLogo`, `builderEstablished`, `builderProjects`, `slug`, `badge`, `customBadgeText`) VALUES (4, 'Nyati Elysia II', 'Nyati Group', 'Nyati Elysia II is a continuation of the successful Elysia project, offering affordable luxury in Kharadi. The project features 1, 2 & 3 BHK apartments with modern architecture and sustainable design. Located near EON IT Park, it offers excellent investment potential.', 'Kharadi', 'Pune', '18.45290000', '73.85670000', 'Under Construction', '78 L - 1.25 Cr', '7800000.00', '12500000.00', '1, 2, 3 BHK', 'P52100045678', '2026-09-01 04:00:00', 450, 5, 18, '/images/projects/project-4.jpg', 'https://www.youtube.com/watch?v=h6vYJFj93RM', 'https://www.nyatigroup.com/brochure/nyati-elysia-brochure.pdf', 1, '2026-01-25 07:18:46', '2026-01-25 09:47:03', NULL, 'Nyati Group is a renowned real estate developer in Pune, established in 2004. With a vision to create exceptional living spaces, they have delivered 25+ residential and commercial projects across Pune. Known for their innovative designs, quality construction, and customer-first approach, Nyati Group has earned the trust of thousands of homeowners. Their projects feature modern architecture, premium amenities, and strategic locations near IT hubs.', '/images/partners/nyati-group.png', 2004, 25, 'shapoorji-pallonji-joyville', NULL, NULL);
INSERT INTO `projects` (`id`, `name`, `builderName`, `description`, `location`, `city`, `latitude`, `longitude`, `status`, `priceRange`, `minPrice`, `maxPrice`, `configurations`, `reraNumber`, `possessionDate`, `totalUnits`, `towers`, `floors`, `coverImage`, `videoUrl`, `brochureUrl`, `featured`, `createdAt`, `updatedAt`, `masterPlanUrl`, `builderDescription`, `builderLogo`, `builderEstablished`, `builderProjects`, `slug`, `badge`, `customBadgeText`) VALUES (5, 'Paranjape Blue Ridge', 'Paranjape Schemes', 'Blue Ridge is an upcoming ultra-luxury township project spanning 50 acres in Kharadi. This integrated township will feature residential towers, a shopping complex, schools, and healthcare facilities. Pre-launch bookings are now open with attractive early bird offers.', 'Kharadi', 'Pune', '18.52040000', '73.85670000', 'Upcoming', '1.1 Cr - 2.8 Cr', '11000000.00', '28000000.00', '2, 3, 4 BHK', 'P52100056789', '2028-11-29 05:00:00', 800, 8, 32, '/images/projects/project-5.jpg', 'https://www.youtube.com/watch?v=NogJrFYaUMQ', 'https://www.pscl.in/brochure/blue-ridge-brochure.pdf', 1, '2026-01-25 07:18:49', '2026-01-25 10:27:37', NULL, 'Paranjape Schemes (Construction) Ltd. is one of Pune\'s oldest and most respected real estate developers, established in 1987. With over 35 years of excellence, they have delivered 60+ landmark projects including the iconic Blue Ridge Township. Known for their integrated townships, sustainable development, and community-focused approach, Paranjape has transformed Pune\'s skyline. Their projects are characterized by thoughtful planning, green spaces, and world-class amenities.', '/images/partners/paranjape-schemes.png', 1987, 60, 'kumar-princetown-royal', 'Premium', NULL);
